mapper.insert:
insert into t_user (
        phone, email, password, salt, createTime
)
values (
         #{phone}, #{email}, #{password}, #{salt},#{ createTime}
);

  insert into t_user_info(
        userId, nick, gender, birth, createTime
  )
  values(
        #{userId}, #{nick}, #{gender}, #{birth}, #{createTime}
)
