mapper.select:
select *
from t_video
where 1 = 1
and

    area = #{area}

limit #{start}, #{size}

select *
from t_video_like
where videoId = #{videoId}
  and userId = #{userId}

mapper.delete:
delete
from t_video_like
where userId = #{userId}
  and videoId = #{videoId}

mapper.select:
select count(1)
from t_video_like
where videoId = #{videoId}

mapper.delete:
delete
from t_video_favourites
where videoId = #{videoId}
  and userId = #{userId}

mapper.select:
select count(1)
from t_video_favourites
where videoId = #{videoId}

select *
from t_video_favourites
where videoId = #{videoId}
  and userId = #{userId}
